/*  layout basics  */
.module            { margin:3rem 0; padding:2rem 1rem; position:relative; }
.module h2         { margin-top:0; font-size:2rem; text-align:center; }

.problem           { margin:2rem 0; }
.problem h3        { margin:0 0 .6rem; font-size:1.3rem; }
.problem details   { margin:.8rem 0 1.2rem; background:#f8f8f8; padding:1rem; border-radius:.5rem; }
.problem details summary { cursor:pointer; font-weight:600; }

.shot              { text-align:center; margin:1rem 0; }
.shot img          { max-width:100%; border:1px solid #ccc; border-radius:.4rem; }

.vote              { display:inline-flex; gap:.4rem; align-items:center; font-size:1rem; }
.vote button       { border:0; background:#eee; padding:.2rem .5rem; line-height:1; cursor:pointer; }
.vote button:hover { background:#ddd; }

/*  artistic separators â€“ dark-purple version  */
/*body        { background:#2E294E; color:#877474; font-family:system-ui, sans-serif; }*/

:root{
  --wb-start:#5349ca;  --wb-end:#92bbdf;  --wb-alpha:.46;
  --dl-start:#E8DFA3;  --dl-end:#E1BCF4;  --dl-alpha:.86; 


  /* RGB triplets (only numbers) for rgba() */
  --wb-start-rgb:83,73,202;  --wb-end-rgb:107,184,255;
  --dl-start-rgb: 232,223,163;   --dl-end-rgb: 225,188,244;
}

.module{
  margin:3rem 0; padding:2rem 1.5rem;
  border-radius:8px;
  box-shadow:0 0 16px rgba(0,0,0,.35);
}
.module h2{ margin-top:0; text-align:center; font-size:1.9rem; }
/* individual module palettes */

.module.website{
  background:linear-gradient(90deg,
      rgba(var(--wb-start-rgb),var(--wb-alpha)) 0%,
      rgba(var(--wb-end-rgb),  var(--wb-alpha)) 100%);
}
.module.dl{
  background:linear-gradient(180deg,
      rgba(var(--dl-start-rgb),var(--dl-alpha)) 0%,
      rgba(var(--dl-end-rgb),  var(--dl-alpha)) 100%);
}

/* inner â€œproblem cardâ€ tweaks so text is readable on bright grads */
.problem{ background:rgba(0,0,0,.21); padding:1.2rem 1rem; border-radius:6px; margin:1.8rem 0; }
.problem h3{ margin:0 0 .7rem; font-size:1.2rem; color:#fff; }
.problem details{ background:rgba(0,0,0,.35); padding:.9rem; border-radius:4px; color:#e8e8e8; }
.problem details code{color:#ffb86c; background:rgba(255,255,255,.10); padding:.1em .25em; border-radius:3px;}
.problem details summary{ cursor:pointer; color:#ffd; }

.problem-sep{border:none;border-top:1px dotted rgba(255,255,255,.4);margin:2.4rem 0;}
.vote{
  display:inline-flex; gap:.6rem; align-items:center;
  font-size:1rem; user-select:none;}

/* universal button look */
.vote button{
  --size: 26px;             /* icon box size   */
  display:flex; align-items:center; justify-content:center;
  width:var(--size); height:var(--size);
  border:none; border-radius:50%;
  background:var(--bg, #ffffff33);   /* will be overridden below */
  transition:background .15s, transform .15s;
  cursor:pointer;
  padding:0;
}

/* icons use currentColor â†’ easy to recolour */
.vote svg{ width:60%; fill:currentColor; }

/* individual tints */
.vote-up   { --bg:#00c85133; color:#00C851; }   /* mint green   */
.vote-down { --bg:#ff444433; color:#FF4444; }   /* coral red    */

/* hover / active feedback */
.vote button:hover{
  background:var(--bg-hover, rgba(255,255,255,.35));
  transform:translateY(-2px);
}
.vote button:active{ transform:translateY(0); }

/* rounded pill around the number */
.vote-count{
  padding:.15rem .55rem;
  background:#ffffff22;
  border-radius:999px;
  min-width:1.8rem;
  text-align:center;
  font-variant-numeric:tabular-nums;
  color:#fff;
  backdrop-filter:blur(2px);
}





const ENDPOINT = "https://hexo-votes-backend.vercel.app/api/vote";

document.querySelectorAll('.vote').forEach(block => {
    const countEl = block.querySelector('.vote-count');
    const upBtn   = block.querySelector('.vote-up');
    const downBtn = block.querySelector('.vote-down');
    const key     = block.parentElement.id;      // e.g. "wb-p1"

    /* 1ï¸âƒ£  initial fetch */
    fetch(`${ENDPOINT}?key=${key}`)
        .then(r => r.json()).then(j => countEl.textContent = j.count)
        .catch(() => countEl.textContent = "?");

    /* helper */
    function send(delta){
        fetch(ENDPOINT, {
            method:"POST",
            headers:{ "Content-Type":"application/json" },
            body: JSON.stringify({ key, delta })
        })
            .then(r => r.json())
            .then(j => countEl.textContent = j.count);
    }

    upBtn  .addEventListener('click', () => send(+1));
    downBtn.addEventListener('click', () => send(-1));
});


