const ENDPOINT = "https://hexo-votes-backend.vercel.app/api/vote";

document.querySelectorAll('.vote').forEach(block=>{
  const key   = block.closest('article').id;
  const count = block.querySelector('.vote-count');
  const send  = d => fetch(ENDPOINT,{
     method:"POST",headers:{'Content-Type':'application/json'},
     body:JSON.stringify({key,delta:d})
  }).then(r=>r.json()).then(j=>count.textContent=j.count);

  fetch(`${ENDPOINT}?key=${key}`)
    .then(r=>r.json()).then(j=>count.textContent=j.count)
    .catch(()=>count.textContent='?');

  block.querySelector('.vote-up').onclick   = () => send(+1);
  block.querySelector('.vote-down').onclick = () => send(-1);
});
